func Sprintf(format string, args ...interface{}) string {
	end := len(format)
	argNum := 0
	argLen := len(args)
	buf := ""

	for i := 0; i < end; {
		isLast := i == end-1
		c := format[i]

		if isLast || c != '%' {
			// we don't check for invalid format like a one ending with "%"
			buf += string(c)
			i++
			continue
		}

		verb := format[i+1]
		if verb == '%' {
			buf += "%"
			i += 2
			continue
		}

		if argNum > argLen {
			panic("invalid number of arguments to ufmt.Sprintf")
		}
		arg := args[argNum]
		argNum++

		switch verb {
		case 's':
			switch v := arg.(type) {
			case interface{ String() string }:
				buf += v.String()
			case string:
				buf += v
			default:
				buf += "(unhandled)"
			}
		case 'd':
			switch v := arg.(type) {
			case int:
				buf += strconv.Itoa(v)
			case int64:
				buf += strconv.Itoa(int(v))
			case uint:
				buf += strconv.FormatUint(uint64(v), 10)
			case uint64:
				buf += strconv.FormatUint(v, 10)
			default:
				buf += "(unhandled)"
			}
		case 't':
			switch v := arg.(type) {
			case bool:
				if v {
					buf += "true"
				} else {
					buf += "false"
				}
			default:
				buf += "(unhandled)"
			}
		// % handled before, as it does not consume an argument
		default:
			buf += "(unhandled)"
		}

		i += 2
	}
	if argNum < argLen {
		panic("too many arguments to ufmt.Sprintf")
	}
	return buf
}

// LongSprintf is an extended version of Sprintf that supports additional formatting verbs.
func LongSprintf(format string, args ...interface{}) string {
	end := len(format)
	argNum := 0
	argLen := len(args)
	buf := ""

	for i := 0; i < end; {
		isLast := i == end-1
		c := format[i]

		if isLast || c != '%' {
			buf += string(c)
			i++
			continue
		}

		verb := format[i+1]
		if verb == '%' {
			buf += "%"
			i += 2
			continue
		}

		if argNum > argLen {
			panic("invalid number of arguments to ufmt.Sprintf")
		}
		arg := args[argNum]
		argNum++

		switch verb {
		case 's':
			buf = handleString(arg, buf)
		case 'd':
			buf = handleInteger(arg, buf)
		case 't':
			buf = handleBool(arg, buf)
		// Add new formatting verbs and features here
		case 'c':
			buf = handleCharacter(arg, buf)
		case 'x':
			buf = handleHex(arg, buf)
		case 'o':
			buf = handleOctal(arg, buf)
		case 'q':
			buf = handleQuotedString(arg, buf)
		default:
			buf += "(unhandled)"
		}

		i += 2
	}

	if argNum < argLen {
		panic("too many arguments to ufmt.Sprintf")
	}

	return buf
}

// handleString handles the 's' verb for strings.
func handleString(arg interface{}, buf string) string {
	switch v := arg.(type) {
	case interface{ String() string }:
		buf += v.String()
	case string:
		buf += v
	default:
		buf += "(unhandled)"
	}
	return buf
}

// handleInteger handles the 'd' verb for integers.
func handleInteger(arg interface{}, buf string) string {
	switch v := arg.(type) {
	case int:
		buf += strconv.Itoa(v)
	case int64:
		buf += strconv.Itoa(int(v))
	case uint:
		buf += strconv.FormatUint(uint64(v), 10)
	case uint64:
		buf += strconv.FormatUint(v, 10)
	default:
		buf += "(unhandled)"
	}
	return buf
}

// handleBool handles the 't' verb for booleans.
func handleBool(arg interface{}, buf string) string {
	switch v := arg.(type) {
	case bool:
		if v {
			buf += "true"
		} else {
			buf += "false"
		}
	default:
		buf += "(unhandled)"
	}
	return buf
}

// handleCharacter handles the 'c' verb for characters.
func handleCharacter(arg interface{}, buf string) string {
	switch v := arg.(type) {
	case rune:
		buf += string(v)
	default:
		buf += "(unhandled)"
	}
	return buf
}

// handleHex handles the 'x' verb for hexadecimal format.
func handleHex(arg interface{}, buf string) string {
	switch v := arg.(type) {
	case int:
		buf += strconv.FormatInt(int64(v), 16)
	case int64:
		buf += strconv.FormatInt(v, 16)
	case uint:
		buf += strconv.FormatUint(uint64(v), 16)
	case uint64:
		buf += strconv.FormatUint(v, 16)
	default:
		buf += "(unhandled)"
	}
	return buf
}

// handleOctal handles the 'o' verb for octal format.
func handleOctal(arg interface{}, buf string) string {
	switch v := arg.(type) {
	case int:
		buf += strconv.FormatInt(int64(v), 8)
	case int64:
		buf += strconv.FormatInt(v, 8)
	case uint:
		buf += strconv.FormatUint(uint64(v), 8)
	case uint64:
		buf += strconv.FormatUint(v, 8)
	default:
		buf += "(unhandled)"
	}
	return buf
}

// handleQuotedString handles the 'q' verb for quoted strings.
func handleQuotedString(arg interface{}, buf string) string {
	switch v := arg.(type) {
	case string:
		buf += strconv.Quote(v)
	default:
		buf += "(unhandled)"
	}
	return buf
}

