package stack

type Stack struct {
	top    *node
	length int
}

type node struct {
	value interface{}
	prev  *node
}

func New() *Stack {
	return &Stack{nil, 0}
}

func (s *Stack) Len() int {
	return s.length
}

func (s *Stack) Top() interface{} {
	if s.length == 0 {
		return nil
	}
	return s.top.value
}

func (s *Stack) Pop() interface{} {
	if s.length == 0 {
		return nil
	}

	node := s.top
	s.top = node.prev
	s.length -= 1
	return node.value
}

func (s *Stack) Push(value interface{}) {
	node := &node{value, s.top}
	s.top = node
	s.length += 1
}

package stack

import (
	"testing"
)

func TestStack(t *testing.T) {
	s := New() // Empty stack

	if s.Len() != 0 {
		t.Errorf("s.Len(): expected 0; got %d", s.Len())
	}

	s.Push(1)

	if s.Len() != 1 {
		t.Errorf("s.Len(): expected 1; got %d", s.Len())
	}

	if top := s.Top(); top.(int) != 1 {
		t.Errorf("s.Top(): expected 1; got %v", top.(int))
	}

	if elem := s.Pop(); elem.(int) != 1 {
		t.Errorf("s.Pop(): expected 1; got %v", elem.(int))
	}
	if s.Len() != 0 {
		t.Errorf("s.Len(): expected 0; got %d", s.Len())
	}
}
