)

func Render(path string) string {
  instance := rand.New()
  num := instance.Int()

  return strconv.Itoa(num)
}

package hello

import (
    "std"
    "testing"
)

func TestPackage(t *testing.T) {
    got := Render("")
    expected := "8835537047539158391"

    if (got != expected) {
        t.Errorf("expected %q; got %q", expected, got)
    }
}

