	caller := std.GetOrigCaller()
	andwell.Approve(caller, spender.Resolve(), amount)
}

func TransferFrom(from, to users.AddressOrName, amount uint64) {
	caller := std.GetOrigCaller()
	andwell.TransferFrom(caller, from.Resolve(), to.Resolve(), amount)
}

